import React from 'react';
import { useState } from 'react';
import { Link} from 'react-router-dom';
import { useNavigate } from 'react-router-dom';



export default function Login() {
  let navigate=useNavigate();

  const [cred, setcred] = useState({ email: "", password: "" });
    const handleSubmit = async (e) => {
        e.preventDefault();
        const response = await fetch("http://localhost:5000/api/loginuser", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ email: cred.email, password: cred.password })
            // left wala key corresponds to backend
        });
        const json = await response.json();
        console.log(json);


        if (!json.success) {
            alert("enter valid credentials")
        }
        else {
          localStorage.setItem("authToken",json.authToken);
          navigate("/");
        }
    }

    const onChange = (event) => {
        setcred({ ...cred, [event.target.name]: event.target.value })
    }
  return (
    <div>
      <h1>login kar bc</h1>
      <div className="container">


                <form onSubmit={handleSubmit}>
                    
                    <div className="mb-3">
                        <label htmlFor="exampleInputEmail1" className="form-label">Email address</label>
                        <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" name="email" value={cred.email} onChange={onChange} />

                    </div>
                    <div className="mb-3">
                        <label htmlFor="exampleInputPassword1" className="form-label">Password</label>
                        <input type="password" className="form-control" name="password" value={cred.password} onChange={onChange} id="exampleInputPassword1" />
                    </div>
                   
                    <button type="submit" className="btn btn-success">Submiit</button>
                    <Link to="/Createuser" className="m-3 btn btn-primary">Sign up</Link>
                </form>
            </div>
      
    </div>
  )
}
