  return (
    <div>
       <div
          className="card mt-3 container "
          style={{ width: "20rem",margin:"1em" ,maxHeight: "415px" }}
        >
          {/* <img src="..." className="card-img-top" alt="..."> */}
          <div className="card-body container">
          <img src={props.imgsrc} className="d-block w-100"  style={{ width: "16rem", height: "14rem" }} alt="..."/>

            <h5 className="card-title">{props.foodname}</h5>
            <p className="card-text" style={{maxHeight: "80px" ,fs:"5px"}}>
              Some quick example text to build on the card title and make up the
              bulk of the card's content.
            </p>
            <div className="container m-0 w-100"  style={{maxHeight: "100px" }}>
              <select className="m-2 w-40 h-80">
                {Array.from(Array(6), (e, i) => {
                  return (
                    <option key={i + 1} value={i + 1}>
                      {" "}
                      {i + 1}{" "}
                    </option>
                  );
                })}
              </select>
              <select className="m-2 w-40 h-80" style={{margin:"50px", maxHeight: "160px" }}>
                {priceOptions.map((data)=>{
                  return <option key={data} value={data}> {data}</option>
                })}
              </select>
              <div className="d-inline  fs-5">Total Price</div>
            </div>
            <button className={'btn btn-success justify-center ms-2'} onClick={handleAddtoCart} >Add To Cart</button>
          </div>
        </div>
    </div>
  )
}
