import React, { useState } from 'react';
import { Link, json } from 'react-router-dom';


export default function Signup() {

    const [cred, setcred] = useState({ name: "", email: "", password: "", geolocation: "" });
    const handleSubmit = async (e) => {
        e.preventDefault();
        const response = await fetch("http://localhost:5000/api/Createuser", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ name: cred.name, email: cred.email, password: cred.password, location: cred.geolocation })
            // left wala key corresponds to backend
        });
        const json = await response.json();
        console.log(json);

        if (!json.success) {
            alert("enter valid credentials")
        }
    }

    const onChange = (event) => {
        setcred({ ...cred, [event.target.name]: event.target.value })
    }

    return (
        <>
            <div className="container">


                <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <label htmlFor="name" className="form-label"> Name</label>
                        <input type="text" className="form-control" name="name" value={cred.name} onChange={onChange} />

                    </div>
                    <div className="mb-3">
                        <label htmlFor="exampleInputEmail1" className="form-label">Email address</label>
                        <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" name="email" value={cred.email} onChange={onChange} />

                    </div>
                    <div className="mb-3">
                        <label htmlFor="exampleInputPassword1" className="form-label">Password</label>
                        <input type="password" className="form-control" name="password" value={cred.password} onChange={onChange} id="exampleInputPassword1" />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="exampleInputPassword1" className="form-label">Address</label>
                        <input type="text" className="form-control" name="address" value={cred.location} onChange={onChange} />
                    </div>
                    <button type="submit" class="btn btn-primary">Submit</button>
                    <Link to="/login" className="m-3 btn btn-primary">Already User?</Link>
                </form>
            </div>

        </>
    )
}
